	int FlipBits_Kandane(int s[], int n)
	{
		int currMax = 0;
		int curr0s = 0;
		int total1s = 0;
		int flux1s = 0;

		for (int i = 0; i < n; ++i)
		{
			if(s[i] == 1)
			{
				if (curr0s - 1 > 0)
				{
					flux1s++;
				}
				else
				{
					total1s = total1s + flux1s;
					total1s++;
					curr0s = 0;
					flux1s = 0;
				}
			}
			else
			{
				curr0s++;
				if(flux1s > 0 && ( curr0s - flux1s > flux1s))
				{
					flux1s = 0;
				}
				if (curr0s > currMax)
					currMax = curr0s;
			}
		}

		return total1s + currMax + flux1s;
	}

